import React from "react";

const StatusBar = ({ session, timerState }) => {
  const getPercentComplete = label => {
    if ((label = "Focusing")) {
      return (
        (timerState.focusDuration * 60 - session.timeRemaining) /
        timerState.focusDuration
      );
    } else {
      return (
        (timerState.breakDuration * 60 - session.timeRemaining) /
        timerState.breakDuration
      );
    }
  };

  return (
    <div className="row mb-2">
      <div className="col">
        <p>{getPercentComplete(session.label)}</p>
        <div className="progress" style={{ height: "20px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow={getPercentComplete("Focusing")} // TODO: Increase aria-valuenow as elapsed time increases
            style={{ width: "%" }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </div>
  );
};

export default StatusBar;
