import React from "react";

const StatusBar = ({ session, timerState, sessionType }) => {
  const focDuration = timerState.focusDuration * 60;
  const breakDuration = timerState.breakDuration * 60;

  const getPercentComplete = label => {
    if ((label = "Focusing")) {
      return (100 * (focDuration - session.timeRemaining)) / focDuration;
    } else if ((label = "On Break")) {
      return (100 * (breakDuration - session.timeRemaining)) / breakDuration;
    }
  };

  return (
    <div className="row mb-2">
      <div className="col">
        <p>{getPercentComplete(session.label)}</p>
        <p>{timerState.focusDuration * 60}</p>
        <p>{session.timeRemaining}</p>
        <div className="progress" style={{ height: "20px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow={getPercentComplete(sessionType)} // TODO: Increase aria-valuenow as elapsed time increases
            style={{ width: getPercentComplete(sessionType) + "%" }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </div>
  );
};

export default StatusBar;
